import React, {Fragment, useState, useContext} from 'react';
import ObraContext from '../../../context/obras/obrasContext';

const NuevoProyecto = () => {

    // obtener el state del formulario
    const ObrasContext = useContext(ObraContext);
    
    const {formulario, errorformulario, mostarFormulario, agregarProyecto, mostrarError} = ObrasContext;

    // state para proyecto
    const [proyecto, guardarPoryecto]= useState({
        nombre:'',
    });

    // extare nombre del proyecto
    const {nombre}= proyecto;
    const onChangeProyecto = e=>{
        guardarPoryecto({
            ...proyecto,
            [e.target.name]: e.target.value

        })
    }

    // cuando el usuario manda un proyecto
    const onSubmitProyecto = e =>{
        e.preventDefault();

        // validar proyecto
        if(nombre===''){
            mostrarError();
            return;
        }
        // agreagr al state
        agregarProyecto(proyecto);
        // reiniciar el form
        guardarPoryecto({
            nombre:''
        })
    }

    // mostrar formulario
    const onClickFormulario = ()=>{
       mostarFormulario();
    }

    return ( 
        <Fragment>
        <button
            type="button"
            className="btn btn-block btn-primario"
            onClick={onClickFormulario}
        >Nuevo Proyecto</button>

        {
            formulario
            ? 
            (
                <form className="formulario-nuevo-proyecto"
                onSubmit={onSubmitProyecto}
                >
                <input
                type="text"
                className="input-text"
                placeholder="Nombre Proyecto"
                name="nombre"
                value={nombre}
                onChange={onChangeProyecto}
                />
                <input
                    type="submit"
                    className="btn btn-primario btn-block"
                    value="Agregar Proyecto"
                />
    
                </form>   
            ): null
        }
        {errorformulario ?<p className="mensaje error">El nombre del Proyecto es obligatiorio</p> : null}
        </Fragment>
     );
}
 
export default NuevoProyecto;
